features.globals:

const double Uint = 1;
const double Omega0 = 2;
const double gamma0 = 1;
const double eta0 = 1.0e-3;
const double mu0 = 1;

vector.initialisation:
potential init_wavefunction

   phi0 = phit0real + i*phit0imag;
   phi1 = phit1real + i*phit1imag;

computed_vector.evaluation:
	wavefunction

    // Calculate the current normalisation of the wave function.
    Ncalc = mod2(phi0) + mod2(phi1);

integrate.operators:
T1 T2 T3

	T1 = -0.5*(eta0+i)*(kx*kx+ky*ky) - (eta0+i)*gamma0*kx;
	T2 = -0.5*(eta0+i)*(kx*kx+ky*ky) + (eta0+i)*gamma0*kx;
	T3 = -(eta0+i)*gamma0*ky;


wavefunction
potential

	dphi0_dt = T1[phi0] + T3[phi1] + (eta0+i)*(mu0-V1-Uint*mod2(phi0)-Uint*mod2(phi1))*phi0 + (eta0+i)*i*Omega0*phi1;
	dphi1_dt = T2[phi1] + T3[phi0] + (eta0+i)*(mu0-V1-Uint*mod2(phi0)-Uint*mod2(phi1))*phi1 - (eta0+i)*i*Omega0*phi0;

output.sampling_group:
	phitt0real phitt0imag phitt1real phitt1imag
	wavefunction 

		phitt0real = Re(phi0);
		phitt0imag = Im(phi0);
    	phitt1real = Re(phi1);
    	phitt1imag = Im(phi1);
